mapper.delete:
delete from lwz_order_info
where id = #{id,jdbcType=INTEGER}
  and order_id = #{orderId,jdbcType=VARCHAR}

mapper.select:
select id, order_id, item_id, item_name, item_amount, order_type, create_time, pay_state, 
pay_type, pay_time
from lwz_order_info


    and id = #{id,jdbcType=INTEGER}


    and order_id = #{orderId,jdbcType=VARCHAR}
